package tdd1;

public class FizzBuzz {

	public String checkFizz(int input) {
		// TODO Auto-generated method stub
		if (input % 3 == 0) {
			return "Fizz";
		}
		return null;
	}

	public String checkBuzz(int input) {
		// TODO Auto-generated method stub
		if (input % 5 == 0) {
			return "Buzz";
		}
		return null;
	}

}
